import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  CircularProgress,
  Paper,
  LinearProgress,
  Stack,
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import AssessmentIcon from '@mui/icons-material/Assessment';
import { styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';

// Styled components
const UploadContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  textAlign: 'center',
  marginTop: theme.spacing(4),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  maxWidth: '800px',
  margin: '0 auto',
}));

const VideoContainer = styled(Paper)(({ theme }) => ({
  marginTop: theme.spacing(4),
  padding: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  width: '100%',
  maxWidth: '1200px',
  margin: '32px auto',
}));

const ProcessingText = styled(Typography)(({ theme }) => ({
  marginTop: theme.spacing(2),
  color: theme.palette.text.secondary,
}));

const Account = () => {
  // State management
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [videoUrl, setVideoUrl] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Load the latest processed video when component mounts
  useEffect(() => {
    // Set the path to the processed video directly
    setVideoUrl('/processed/result_out.mp4');
  }, []);

  // Handle file upload and processing
  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploading(true);
    setProcessing(true);
    setProgress(0);
    setError(null);

    try {
      // Create form data
      const formData = new FormData();
      formData.append('video', file);

      // Upload and process the video
      const response = await fetch('http://localhost:5000/api/process-video', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to process video');
      }

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.error || 'Failed to process video');
      }

      // Set the video URL directly from the processed folder
      setVideoUrl(`/processed/${data.filename}`);
      setProgress(100);

    } catch (error) {
      console.error('Error processing video:', error);
      setError('Failed to process video. Please try again.');
    } finally {
      setUploading(false);
      setProcessing(false);
      event.target.value = '';
    }
  };

  // Handle video download
  const handleDownload = async () => {
    if (!videoUrl) return;

    try {
      // Create a link element
      const link = document.createElement('a');
      link.href = videoUrl;
      link.download = 'processed_video.mp4'; // Set the download filename
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error('Error downloading video:', error);
      setError('Failed to download video');
    }
  };

  // Handle navigation to results page
  const handleShowResults = () => {
    navigate('/results'); // Navigate to the results page
  };

  // Get status text for the upload button
  const getStatusText = () => {
    if (error) return "Try Again";
    if (uploading) return "Uploading...";
    if (processing) {
      if (progress < 30) return "Initializing...";
      if (progress < 60) return "Processing...";
      if (progress < 90) return "Finalizing...";
      return "Almost Done...";
    }
    return "Upload Video";
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* Page Title */}
      <Typography variant="h3" component="h1" gutterBottom textAlign="center">
        Video Processing
      </Typography>

      {/* Upload Section */}
      <UploadContainer elevation={3}>
        <Typography variant="h5" gutterBottom>
          Upload Video for Processing
        </Typography>

        {/* File Input */}
        <input
          type="file"
          accept="video/mp4"
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          id="video-upload"
        />
        <label htmlFor="video-upload">
          <Button
            component="span"
            variant="contained"
            size="large"
            disabled={uploading || processing}
            startIcon={uploading || processing ? <CircularProgress size={20} /> : <CloudUploadIcon />}
            sx={{ mt: 2 }}
          >
            {getStatusText()}
          </Button>
        </label>

        {/* Progress Bar */}
        {(uploading || processing) && (
          <Box sx={{ width: '100%', mt: 3 }}>
            <LinearProgress 
              variant="determinate" 
              value={progress} 
              sx={{ height: 8, borderRadius: 4 }}
            />
            <ProcessingText variant="body2">
              {uploading ? 'Uploading...' : `Processing: ${progress}%`}
            </ProcessingText>
          </Box>
        )}

        {/* Error Message */}
        {error && (
          <Typography color="error" sx={{ mt: 2 }}>
            {error}
          </Typography>
        )}
      </UploadContainer>

      {/* Video Display */}
      {videoUrl && (
        <VideoContainer elevation={3}>
          <Typography variant="h6" gutterBottom sx={{ textAlign: 'center' }}>
            Processed Video
          </Typography>
          <Box sx={{ 
            width: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 2,
          }}>
            <video
              controls
              autoPlay
              key={videoUrl}
              src={videoUrl}
              style={{
                width: '100%',
                maxHeight: '70vh',
                objectFit: 'contain',
                backgroundColor: '#000',
                borderRadius: '8px',
              }}
            >
              Your browser does not support the video tag.
            </video>

            {/* Action Buttons */}
            <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
              <Button
                variant="contained"
                color="primary"
                startIcon={<CloudDownloadIcon />}
                onClick={handleDownload}
              >
                Download Video
              </Button>
              <Button
                variant="contained"
                color="secondary"
                startIcon={<AssessmentIcon />}
                onClick={handleShowResults}
              >
                Show Results
              </Button>
            </Stack>
          </Box>
        </VideoContainer>
      )}
    </Container>
  );
};

export default Account;
