import React from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
} from '@mui/material';
import StopIcon from '@mui/icons-material/Stop';
import WarningIcon from '@mui/icons-material/Warning';
import InfoIcon from '@mui/icons-material/Info';
import DoNotDisturbIcon from '@mui/icons-material/DoNotDisturb';

const Features = () => {
  const trafficSigns = [
    {
      category: "Regulatory Signs",
      description: "These signs give orders which must be obeyed",
      signs: [
        {
          name: "Stop Sign",
          description: "Complete stop required. Yield right-of-way to all other traffic and pedestrians before proceeding.",
          icon: <StopIcon sx={{ fontSize: 56, color: '#ff0000' }} />,
          color: '#ff0000'
        },
        {
          name: "Yield Sign",
          description: "Slow down and yield right-of-way to all traffic and pedestrians before proceeding.",
          icon: <DoNotDisturbIcon sx={{ fontSize: 56, color: '#ff0000' }} />,
          color: '#ff0000'
        },
        {
          name: "Speed Limit",
          description: "Indicates the maximum legal speed limit for the road under ideal conditions.",
          icon: <InfoIcon sx={{ fontSize: 56, color: '#000000' }} />,
          color: '#000000'
        }
      ]
    },
    {
      category: "Warning Signs",
      description: "These signs warn of potential hazards ahead",
      signs: [
        {
          name: "Pedestrian Crossing",
          description: "Warning for drivers to watch for pedestrians crossing the road.",
          icon: <WarningIcon sx={{ fontSize: 56, color: '#ffd700' }} />,
          color: '#ffd700'
        },
        {
          name: "Sharp Curve",
          description: "Warning of a dangerous curve ahead. Reduce speed and stay in lane.",
          icon: <WarningIcon sx={{ fontSize: 56, color: '#ffd700' }} />,
          color: '#ffd700'
        },
        {
          name: "School Zone",
          description: "Warning of a school zone ahead. Watch for children and reduce speed.",
          icon: <WarningIcon sx={{ fontSize: 56, color: '#ffd700' }} />,
          color: '#ffd700'
        }
      ]
    },
    {
      category: "Information Signs",
      description: "These signs provide helpful information for drivers",
      signs: [
        {
          name: "Hospital",
          description: "Indicates the direction to the nearest hospital or emergency medical facility.",
          icon: <InfoIcon sx={{ fontSize: 56, color: '#0066cc' }} />,
          color: '#0066cc'
        },
        {
          name: "Parking",
          description: "Indicates parking areas or parking regulations.",
          icon: <InfoIcon sx={{ fontSize: 56, color: '#0066cc' }} />,
          color: '#0066cc'
        },
        {
          name: "Gas Station",
          description: "Indicates the direction to the nearest gas/fuel station.",
          icon: <InfoIcon sx={{ fontSize: 56, color: '#0066cc' }} />,
          color: '#0066cc'
        }
      ]
    }
  ];

  return (
    <Box>
      {/* Hero Section */}
      <Box sx={{ bgcolor: 'primary.main', color: 'white', py: 12 }}>
        <Container maxWidth="lg">
          <Typography variant="h2" component="h1" textAlign="center" gutterBottom>
            Traffic Signs & Safety
          </Typography>
          <Typography variant="h5" textAlign="center" sx={{ mb: 4, maxWidth: '800px', mx: 'auto' }}>
            Understanding traffic signs is crucial for road safety. Learn about different types of traffic signs and their meanings.
          </Typography>
        </Container>
      </Box>

      {/* Traffic Signs Categories */}
      <Container maxWidth="lg">
        {trafficSigns.map((category, index) => (
          <Box key={index} sx={{ py: 8 }}>
            <Typography variant="h3" gutterBottom>
              {category.category}
            </Typography>
            <Typography variant="body1" paragraph sx={{ mb: 4 }}>
              {category.description}
            </Typography>
            <Grid container spacing={4}>
              {category.signs.map((sign, signIndex) => (
                <Grid item xs={12} md={4} key={signIndex}>
                  <Card sx={{ height: '100%' }}>
                    <CardContent sx={{ textAlign: 'center', py: 4 }}>
                      <Box sx={{ mb: 2 }}>
                        {sign.icon}
                      </Box>
                      <Typography variant="h5" gutterBottom>
                        {sign.name}
                      </Typography>
                      <Typography color="text.secondary">
                        {sign.description}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        ))}
      </Container>

      {/* Safety Tips Section */}
      <Box sx={{ bgcolor: 'grey.100', py: 8 }}>
        <Container maxWidth="lg">
          <Typography variant="h3" gutterBottom textAlign="center">
            Road Safety Tips
          </Typography>
          <Grid container spacing={4} sx={{ mt: 2 }}>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Always Stay Alert
                  </Typography>
                  <Typography>
                    Pay attention to road signs and your surroundings at all times while driving.
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Follow Speed Limits
                  </Typography>
                  <Typography>
                    Observe posted speed limits and adjust your speed according to weather conditions.
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Maintain Safe Distance
                  </Typography>
                  <Typography>
                    Keep a safe following distance from the vehicle ahead of you.
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </Box>
  );
};

export default Features;
