import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  CircularProgress,
  Paper,
  LinearProgress,
  Grid,
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import { styled } from '@mui/material/styles';
import Footer from '../components/Footer';

const UploadContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  textAlign: 'center',
  marginTop: theme.spacing(4),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
}));

const VideoContainer = styled(Paper)(({ theme }) => ({
  marginTop: theme.spacing(4),
  padding: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  width: '100%',
  maxWidth: '1600px',
  margin: '32px auto',
  '& video': {
    width: '100%',
    maxHeight: '70vh',
    objectFit: 'contain',
  }
}));

const ProcessingText = styled(Typography)(({ theme }) => ({
  marginTop: theme.spacing(2),
  color: theme.palette.text.secondary,
}));

const Account = () => {
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [resultVideo, setResultVideo] = useState(null);
  const [allVideos, setAllVideos] = useState([]);
  const [progress, setProgress] = useState(0);

  // Load all videos when component mounts
  useEffect(() => {
    loadAllVideos();
  }, []);

  const loadAllVideos = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/all-videos');
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setAllVideos(data.videos);
          // Set the latest video as the current result video if we don't have one yet
          if (!resultVideo && data.videos.length > 0) {
            setResultVideo(`http://localhost:5000${data.videos[0].video_url}`);
          }
        }
      }
    } catch (error) {
      console.error('Error loading videos:', error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploading(true);
    setProgress(0);
    
    try {
      // Create form data
      const formData = new FormData();
      formData.append('video', file);

      // Send to backend
      const response = await fetch('http://localhost:5000/api/process-video', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to process video');
      }

      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.error || 'Failed to process video');
      }

      // Set the video URL from the processed folder
      const videoUrl = `http://localhost:5000${data.video_url}`;
      setResultVideo(videoUrl);
      
      // Refresh the videos list
      loadAllVideos();
      
      setProcessing(false);
      setProgress(100);
      
    } catch (error) {
      console.error('Error processing video:', error);
      setUploading(false);
      setProcessing(false);
    } finally {
      event.target.value = '';
    }
  };

  const selectVideo = (videoUrl) => {
    setResultVideo(`http://localhost:5000${videoUrl}`);
  };

  const getStatusText = () => {
    if (uploading) return "Uploading video...";
    if (processing) {
      if (progress < 30) return "Initializing processing...";
      if (progress < 60) return "Processing video...";
      if (progress < 90) return "Analyzing results...";
      return "Finalizing...";
    }
    return "Upload Video";
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column',
      minHeight: '100vh'
    }}>
      <Container maxWidth="lg" sx={{ flex: 1, py: 4 }}>
        <Box sx={{ py: 8 }}>
          <Typography variant="h3" component="h1" gutterBottom textAlign="center">
            My Account
          </Typography>

          <UploadContainer elevation={3} sx={{ maxWidth: '800px', mx: 'auto' }}>
            <Typography variant="h5" gutterBottom>
              Upload Video for Processing
            </Typography>
            <input
              type="file"
              accept="video/mp4"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
              id="video-upload"
            />
            <label htmlFor="video-upload">
              <Button
                component="span"
                variant="contained"
                size="large"
                disabled={uploading || processing}
                startIcon={(uploading || processing) ? <CircularProgress size={20} /> : <CloudUploadIcon />}
                sx={{ mt: 2 }}
              >
                {getStatusText()}
              </Button>
            </label>
            
            {(uploading || processing) && (
              <Box sx={{ width: '100%', mt: 3 }}>
                <LinearProgress 
                  variant="determinate" 
                  value={processing ? progress : 0} 
                  sx={{ height: 8, borderRadius: 4 }}
                />
                <ProcessingText variant="body2">
                  {processing ? `Progress: ${progress}%` : 'Uploading...'}
                </ProcessingText>
              </Box>
            )}
          </UploadContainer>

          {resultVideo && (
            <VideoContainer elevation={3}>
              <Typography variant="h6" gutterBottom sx={{ textAlign: 'center' }}>
                Currently Selected Video
              </Typography>
              <Box sx={{ 
                width: '100%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                overflow: 'hidden'
              }}>
                <video
                  controls
                  key={resultVideo}
                  src={resultVideo}
                  style={{ 
                    borderRadius: '8px',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                    width: '100%',
                    maxHeight: '70vh',
                    objectFit: 'contain'
                  }}
                >
                  Your browser does not support the video tag.
                </video>
              </Box>
            </VideoContainer>
          )}

          {allVideos.length > 0 && (
            <VideoContainer elevation={3}>
              <Typography variant="h6" gutterBottom sx={{ textAlign: 'center' }}>
                All Processed Videos
              </Typography>
              <Grid container spacing={2}>
                {allVideos.map((video, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Paper 
                      elevation={2} 
                      sx={{ 
                        p: 2, 
                        cursor: 'pointer',
                        borderRadius: '8px',
                        transition: 'all 0.2s',
                        '&:hover': {
                          boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
                        }
                      }}
                      onClick={() => selectVideo(video.video_url)}
                    >
                      <Typography variant="subtitle1" gutterBottom>
                        {video.filename}
                      </Typography>
                      <video
                        width="100%"
                        height="160"
                        style={{ objectFit: 'cover', borderRadius: '4px' }}
                        src={`http://localhost:5000${video.video_url}`}
                      >
                        Your browser does not support the video tag.
                      </video>
                      <Button 
                        variant="outlined" 
                        fullWidth 
                        sx={{ mt: 1 }}
                        onClick={(e) => {
                          e.stopPropagation();
                          selectVideo(video.video_url);
                        }}
                      >
                        Select Video
                      </Button>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </VideoContainer>
          )}
        </Box>
      </Container>
      <Footer />
    </Box>
  );
};

export default Account;
