import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  CircularProgress,
  Paper,
  LinearProgress,
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { styled } from '@mui/material/styles';
import Footer from '../components/Footer';

const UploadContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  textAlign: 'center',
  marginTop: theme.spacing(4),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
}));

const ProcessingText = styled(Typography)(({ theme }) => ({
  marginTop: theme.spacing(2),
  color: theme.palette.text.secondary,
}));

const Account = () => {
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploading(true);
    setProgress(0);

    try {
      // Create form data and send to backend
      const formData = new FormData();
      formData.append('video', file);

      // Send to backend
      const response = await fetch('http://localhost:5000/api/process-video', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to process video');
      }

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.error || 'Failed to process video');
      }

      // First show uploading state
      await new Promise(resolve => setTimeout(resolve, 2000));
      setUploading(false);

      // Start processing with random progress
      setProcessing(true);

    } catch (error) {
      console.error('Error processing video:', error);
      setUploading(false);
      setProcessing(false);
    } finally {
      event.target.value = '';
    }
  };

  const getStatusText = () => {
    if (uploading) return "Uploading video...";
    if (processing) {
      if (progress < 30) return "Initializing processing...";
      if (progress < 60) return "Processing video...";
      if (progress < 90) return "Analyzing results...";
      return "Finalizing...";
    }
    return "Upload Video";
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column',
      minHeight: '100vh'
    }}>
      <Container maxWidth="lg" sx={{ flex: 1, py: 4 }}>
        <Box sx={{ py: 8 }}>
          <Typography variant="h3" component="h1" gutterBottom textAlign="center">
            My Account
          </Typography>

          <UploadContainer elevation={3} sx={{ maxWidth: '800px', mx: 'auto' }}>
            <Typography variant="h5" gutterBottom>
              Upload Video for Processing
            </Typography>
            <input
              type="file"
              accept="video/mp4"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
              id="video-upload"
            />
            <label htmlFor="video-upload">
              <Button
                component="span"
                variant="contained"
                size="large"
                disabled={uploading || processing}
                startIcon={(uploading || processing) ? <CircularProgress size={20} /> : <CloudUploadIcon />}
                sx={{ mt: 2 }}
              >
                {getStatusText()}
              </Button>
            </label>

            {(uploading || processing) && (
              <Box sx={{ width: '100%', mt: 3 }}>
                <LinearProgress 
                  variant="determinate" 
                  value={processing ? progress : 0} 
                  sx={{ height: 8, borderRadius: 4 }}
                />
                <ProcessingText variant="body2">
                  {processing ? `Progress: ${progress}%` : 'Uploading...'}
                </ProcessingText>
              </Box>
            )}
          </UploadContainer>
        </Box>
      </Container>
      <Footer />
    </Box>
  );
};

export default Account;
