import React, { useRef, useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  TextField,
  CircularProgress,
  Snackbar,
  Alert,
  Paper,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import SpeedIcon from '@mui/icons-material/Speed';
import SecurityIcon from '@mui/icons-material/Security';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';

const StyledButton = styled(Button)(({ theme }) => ({
  borderRadius: '24px',
  padding: '10px 24px',
  textTransform: 'none',
  fontSize: '16px',
}));

const HeroSection = styled(Box)(({ theme }) => ({
  minHeight: '80vh',
  display: 'flex',
  alignItems: 'center',
  position: 'relative',
  color: 'white',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: 'url("/WhatsApp Image 2025-04-22 at 12.40.40_f427b15e.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    zIndex: -2,
  },
  '&::after': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'rgba(0, 0, 0, 0.4)',
    zIndex: -1,
  },
}));

const VideoContainer = styled(Paper)(({ theme }) => ({
  marginTop: theme.spacing(4),
  padding: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  borderRadius: theme.spacing(1),
}));

const Home = () => {
  const fileInputRef = useRef(null);
  const [uploading, setUploading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'info' });
  const [processedVideoUrl, setProcessedVideoUrl] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleStartProcessing = () => {
    fileInputRef.current?.click();
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('video/')) {
      setSnackbar({
        open: true,
        message: 'Please upload a video file',
        severity: 'error'
      });
      return;
    }

    setUploading(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('video', file);

    try {
      const response = await fetch('http://localhost:5000/process-video', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to process video');
      }

      const videoBlob = await response.blob();
      const videoUrl = URL.createObjectURL(videoBlob);
      setProcessedVideoUrl(videoUrl);
      
      setSnackbar({
        open: true,
        message: 'Video processed successfully!',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Error processing video. Please try again.',
        severity: 'error'
      });
    } finally {
      setUploading(false);
      setUploadProgress(0);
      event.target.value = '';
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbar(prev => ({ ...prev, open: false }));
  };

  return (
    <Box>
      <HeroSection>
        <Container>
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography 
                variant="h2" 
                component="h1" 
                gutterBottom
                sx={{ 
                  textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
                  fontWeight: 'bold'
                }}
              >
                Transform Your Videos with AI
              </Typography>
              <Typography 
                variant="h5" 
                paragraph
                sx={{ 
                  textShadow: '1px 1px 2px rgba(0,0,0,0.3)',
                  mb: 3
                }}
              >
                Powerful video processing tools powered by artificial intelligence.
                Edit, enhance, and optimize your videos in minutes.
              </Typography>
              <input
                type="file"
                ref={fileInputRef}
                style={{ display: 'none' }}
                accept="video/*"
                onChange={handleFileUpload}
              />
              <Button
                variant="contained"
                size="large"
                onClick={handleStartProcessing}
                disabled={uploading}
                startIcon={uploading ? <CircularProgress size={20} /> : <CloudUploadIcon />}
                sx={{
                  mt: 2,
                  bgcolor: 'white',
                  color: 'primary.main',
                  '&:hover': {
                    bgcolor: 'grey.100',
                  },
                  boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                }}
              >
                {uploading ? 'Processing...' : 'Start Processing'}
              </Button>

              {processedVideoUrl && (
                <VideoContainer>
                  <Typography variant="h6" gutterBottom sx={{ color: 'text.primary' }}>
                    Processed Video
                  </Typography>
                  <video
                    controls
                    width="100%"
                    src={processedVideoUrl}
                    style={{ borderRadius: '8px' }}
                  >
                    Your browser does not support the video tag.
                  </video>
                </VideoContainer>
              )}
            </Grid>
            <Grid item xs={12} md={6}>
              {/* Add hero image here */}
            </Grid>
          </Grid>
        </Container>
      </HeroSection>

      {/* Features Section */}
      <Container maxWidth="lg">
        <Box sx={{ py: 8 }}>
          <Typography variant="h3" component="h2" textAlign="center" gutterBottom>
            Why Choose Our Platform
          </Typography>
          <Typography variant="h6" color="text.secondary" textAlign="center" sx={{ mb: 6 }}>
            Experience the next generation of video processing technology
          </Typography>
          <Grid container spacing={4}>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent sx={{ textAlign: 'center', py: 4 }}>
                  <SpeedIcon color="primary" sx={{ fontSize: 56, mb: 2 }} />
                  <Typography variant="h5" gutterBottom>
                    Lightning Fast
                  </Typography>
                  <Typography color="text.secondary">
                    Process your videos in real-time with our optimized algorithms
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent sx={{ textAlign: 'center', py: 4 }}>
                  <SecurityIcon color="primary" sx={{ fontSize: 56, mb: 2 }} />
                  <Typography variant="h5" gutterBottom>
                    Secure Processing
                  </Typography>
                  <Typography color="text.secondary">
                    Your content is protected with enterprise-grade security
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ height: '100%' }}>
                <CardContent sx={{ textAlign: 'center', py: 4 }}>
                  <AnalyticsIcon color="primary" sx={{ fontSize: 56, mb: 2 }} />
                  <Typography variant="h5" gutterBottom>
                    Advanced Analytics
                  </Typography>
                  <Typography color="text.secondary">
                    Get detailed insights about your processed videos
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Box>
      </Container>

      {/* Stats Section */}
      <Box sx={{ bgcolor: 'primary.main', color: 'white', py: 8 }}>
        <Container maxWidth="lg">
          <Grid container spacing={4} justifyContent="center">
            <Grid item xs={12} md={4} textAlign="center">
              <Typography variant="h2" fontWeight="bold">99%</Typography>
              <Typography variant="h6">Processing Accuracy</Typography>
            </Grid>
            <Grid item xs={12} md={4} textAlign="center">
              <Typography variant="h2" fontWeight="bold">24/7</Typography>
              <Typography variant="h6">Available Support</Typography>
            </Grid>
            <Grid item xs={12} md={4} textAlign="center">
              <Typography variant="h2" fontWeight="bold">100ms</Typography>
              <Typography variant="h6">Processing Speed</Typography>
            </Grid>
          </Grid>
        </Container>
      </Box>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Home;
